import React from "react";
import { connect } from "react-redux";
import WebSocketInstance from "../websocket";
import Hoc from "../hoc/hoc";

class Chat extends React.Component {
  state = { message: "" };

  initialiseChat() {
    this.waitForSocketConnection(() => {
      WebSocketInstance.fetchMessages(
        this.props.username,
        this.props.match.params.chatID
      );
    });
    WebSocketInstance.connect(this.props.match.params.chatID);
  }

  constructor(props) {
    super(props);
    this.initialiseChat();
  }

  waitForSocketConnection(callback) {
    const component = this;
    setTimeout(function () {
      if (WebSocketInstance.state() === 1) {
        console.log("Connection is made");
        callback();
        return;
      } else {
        console.log("wait for connection...");
        component.waitForSocketConnection(callback);
      }
    }, 100);
  }

  messageChangeHandler = event => {
    this.setState({ message: event.target.value });
  };

  sendMessageHandler = e => {
    e.preventDefault();
    const messageObject = {
      from: this.props.username,
      content: this.state.message,
      chatId: this.props.match.params.chatID
    };
    WebSocketInstance.newChatMessage(messageObject);
    this.setState({ message: "" });
  };

  renderTimestamp = timestamp => {
    let prefix = "";
    const timeDiff = Math.round(
      (new Date().getTime() - new Date(timestamp).getTime()) / 60000
    );
    if (timeDiff < 1) {
      // less than one minute ago
      prefix = "just now...";
    } else if (timeDiff < 60 && timeDiff > 1) {
      // less than sixty minutes ago
      prefix = `${timeDiff} minutes ago`;
    } else if (timeDiff < 24 * 60 && timeDiff > 60) {
      // less than 24 hours ago
      prefix = `${Math.round(timeDiff / 60)} hours ago`;
    } else if (timeDiff < 31 * 24 * 60 && timeDiff > 24 * 60) {
      // less than 7 days ago
      prefix = `${Math.round(timeDiff / (60 * 24))} days ago`;
    } else {
      prefix = `${new Date(timestamp)}`;
    }
    return prefix;
  };

  renderMessages = messages => {
    const currentUser = this.props.username;
    return messages.map((message, i, arr) => (
      <li
        key={message.id}
        style={{ marginBottom: arr.length - 1 === i ? "300px" : "15px" }}
        className={message.author === currentUser ? "sent" : "replies"}
      >
        <img
          src=""
          alt=""
        />
        <p>
          {message.content}
          <br />
          <small>{this.renderTimestamp(message.timestamp)}</small>
        </p>
      </li>
    ));
  };

  scrollToBottom = () => {
    this.messagesEnd.scrollIntoView({ behavior: "smooth" });
  };

  componentDidMount() {
    this.scrollToBottom();
  }

  componentDidUpdate() {
    this.scrollToBottom();
  }

  componentWillReceiveProps(newProps) {
    if (this.props.match.params.chatID !== newProps.match.params.chatID) {
      WebSocketInstance.disconnect();
      this.waitForSocketConnection(() => {
        WebSocketInstance.fetchMessages(
          this.props.username,
          newProps.match.params.chatID
        );
      });
      WebSocketInstance.connect(newProps.match.params.chatID);
    }
  }

  render() {
    return (
      <Hoc>
        <div className="messages">
          <ul id="chat-log">
            {this.props.messages && this.renderMessages(this.props.messages)}
            <div
              style={{ float: "left", clear: "both" }}
              ref={el => {
                this.messagesEnd = el;
              }}
            />
          </ul>
          <div class="container">
            <div id="chronometer" class="chronometer">
              <div class="time">
                <div class="counter-down">
                  <div class="box">
                    <div id="hour-first-digit" class="left">1</div>
                    <div id="hour-second-digit" class="right">0</div>
                  </div>
                  <div class="description">HOURS</div>
                </div>
                <div class="counter-down">
                  <div class="box">
                    <div id="minute-first-digit" class="left">0</div>
                    <div id="minute-second-digit" class="right">0</div>
                  </div>
                  <div class="description">MINUTES</div>
                </div>
                <div class="counter-down">
                  <div class="box">
                    <div id="second-first-digit" class="left">0</div>
                    <div id="second-second-digit" class="right">0</div>
                  </div>
                  <div class="description">SECONDS</div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="message-input">
          <form onSubmit={this.sendMessageHandler}>
            <div className="wrap">
              <input
                onChange={this.messageChangeHandler}
                value={this.state.message}
                required
                id="chat-message-input"
                type="text"
                placeholder="Escribe tu mensaje..."
              />
              <i className="fa fa-paperclip attachment" aria-hidden="true" />
              <button id="chat-message-submit" className="submit">
                <i className="fa fa-paper-plane" aria-hidden="true" />
              </button>
            </div>
          </form>
        </div>
      </Hoc>
    );
  }
}

// Cronometro

var currentDate;
var hours = 14;
var minutes = 27;
var seconds = 54;
var counter = 0;

setInterval(() => {
  counter++;
  var minutesTimeSpan = counter * 1000;
  currentDate = new Date();
  currentDate.setTime(currentDate.getTime() + minutesTimeSpan);
  countDownDate = currentDate.getTime();
  refreshTime();
}, 1000);

function refreshTime() {
  var now = new Date().getTime();
  var distance = countDownDate - now;

  var days = Math.floor(distance / (1000 * 60 * 60 * 24));
  var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
  var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
  var seconds = Math.floor((distance % (1000 * 60)) / 1000);

  updateDOM(hours, minutes, seconds);
}

function updateDOM(hours, minutes, seconds) {
  if (hours.toString().length == 2) {
    document.getElementById("hour-first-digit").innerHTML = hours.toString().split("")[0];
    document.getElementById("hour-second-digit").innerHTML = hours.toString().split("")[1];
  } else {
    document.getElementById("hour-first-digit").innerHTML = "0";
    document.getElementById("hour-second-digit").innerHTML = hours.toString();
  }

  if (minutes.toString().length == 2) {
    document.getElementById("minute-first-digit").innerHTML = minutes.toString().split("")[0];
    document.getElementById("minute-second-digit").innerHTML = minutes.toString().split("")[1];
  } else {
    document.getElementById("minute-first-digit").innerHTML = "0";
    document.getElementById("minute-second-digit").innerHTML = minutes.toString();
  }

  if (seconds.toString().length == 2) {
    document.getElementById("second-first-digit").innerHTML = seconds.toString().split("")[0];
    document.getElementById("second-second-digit").innerHTML = seconds.toString().split("")[1];
  } else {
    document.getElementById("second-first-digit").innerHTML = "0";
    document.getElementById("second-second-digit").innerHTML = seconds.toString();
  }
}
//Final cronometro

const mapStateToProps = state => {
  return {
    username: state.auth.username,
    messages: state.message.messages
  };
};

export default connect(mapStateToProps)(Chat);
