import React from "react";
import { Spin, Icon } from "antd";
import { connect } from "react-redux";
import * as actions from "../store/actions/auth";
import * as navActions from "../store/actions/nav";
import * as messageActions from "../store/actions/message";
import Contact from "../components/Contact";

const antIcon = <Icon type="loading" style={{ fontSize: 24 }} spin />;

class Sidepanel extends React.Component {
  state = {
    loginForm: true
  };

  waitForAuthDetails() {
    const component = this;
    setTimeout(function () {
      if (
        component.props.token !== null &&
        component.props.token !== undefined
      ) {
        component.props.getUserChats(
          component.props.username,
          component.props.token
        );
        return;
      } else {
        console.log("waiting for authentication details...");
        component.waitForAuthDetails();
      }
    }, 100);
  }

  componentDidMount() {
    this.waitForAuthDetails();
  }

  openAddChatPopup() {
    this.props.addChat();
  }

  changeForm = () => {
    this.setState({ loginForm: !this.state.loginForm });
  };

  authenticate = e => {
    e.preventDefault();
    if (this.state.loginForm) {
      this.props.login(e.target.username.value, e.target.password.value);
    } else {
      this.props.signup(
        e.target.username.value,
        e.target.email.value,
        e.target.password.value,
        e.target.password2.value
      );
    }
  };

  render() {
    let activeChats = this.props.chats.map(c => {
      return (
        <Contact
          key={c.id}
          name="Usuario"
          picURL="https://www.flaticon.es/icono-gratis/imagen-de-usuario-con-fondo-negro_17004"
          status="online"
          chatURL={`/${c.id}`}
        />
      );
    });
    return (
      <div id="sidepanel">
        <div id="profile">
          <div className="wrap">
            <img
              id="profile-img"
              src="../219983.png"
              className="online"
              alt=""
            />
            <p>Ingresar</p>
            <div id="status-options">
              <ul>
                <li id="status-online" className="active">
                  <span className="status-circle" /> <p>En linea</p>
                </li>
                <li id="status-away">
                  <span className="status-circle" /> <p>Lejos</p>
                </li>
                <li id="status-busy">
                  <span className="status-circle" /> <p>Ocupado</p>
                </li>
                <li id="status-offline">
                  <span className="status-circle" /> <p>Fuera de linea</p>
                </li>
              </ul>
            </div>
            <div id="expanded">
              {this.props.loading ? (
                <Spin indicator={antIcon} />
              ) : this.props.isAuthenticated ? (
                <button onClick={() => this.props.logout()} className="authBtn">
                  <span>Salir</span>
                </button>
              ) : (
                <div>
                  <form method="POST" onSubmit={this.authenticate}>
                    <button type="button" id="btn-abrir-popup1" class="btn-abrir-popup"
                      onClick={() => abrir1()}>Log In</button>
                    <div class="overlay" id="overlay1">
                      <div class="popup" id="popup1">
                        <a href="#" id="btn-cerrar-popup1" class="btn-cerrar-popup">X<i></i></a>
                        <form method="POST" onSubmit={this.authenticate}>
                          <div>
                            <input name="username" type="text" placeholder="Usuario" />
                            <input name="password" type="password" placeholder="Contraseña" />
                          </div>
                          <button type="submit">Verificar</button>
                        </form>
                      </div>
                    </div>
                    <button type="button" id="btn-abrir-popup2" class="btn-abrir-popup" onClick={() => abrir2()}>Añadir Usuario</button>
                    <div class="overlay" id="overlay2">
                      <div class="popup" id="popup2">
                        <a href="#" id="btn-cerrar-popup2" class="btn-cerrar-popup">X<i></i></a>
                        <div>
                          <input name="username" type="text" placeholder="Usuario" />
                          <input name="email" type="email" placeholder="Email" />
                          <input name="password" type="password" placeholder="Contraseña" />
                          <input name="password2" type="password" placeholder="Verificar contraseña" />
                        </div>
                        <button type="button">Añadir Usuario</button>
                      </div>
                    </div>
                    <button type="button" id="btn-abrir-popup3" class="btn-abrir-popup" onClick={() => abrir3()}>RfID</button>
                    <div class="overlay" id="overlay3">
                      <div class="popup" id="popup3">
                        <a href="#" id="btn-cerrar-popup3" class="btn-cerrar-popup">X<i></i></a>
                        <input type="text" placeholder="Acerque la tarjeta" />
                        <p class="mensaje">Tu usuario es:</p><input type="text" placeholder="#######" />
                      </div>
                    </div>
                  </form>
                </div>
              )}
            </div>
          </div>
        </div>
        <div id="search">
